INSERT IGNORE INTO `%TABLE_PREFIX%iwcc_cookie` (`pid`, `id`, `clang_id`, `uid`, `service_name`, `provider`, `provider_link_privacy`, `definition`) VALUES
(1,1,1,'iwcc','Datenschutz Cookie','Diese Website','','-\n name: iwcc\n time: \"1 Jahr\"\n desc: \"Speichert Ihre Auswahl bzgl. der Cookies.\"'),
(2,2,1,'google-analytics','Google Analytics','Google','','-\n name: _ga\n time: \"2 Jahre\"\n desc: \"Speichert für jeden Besucher der Website eine anonyme ID. Anhand der ID können Seitenaufrufe einem Besucher zugeordnet werden.\"\n-\n name: _gat\n time: \"1 Tag\"\n desc: \"Verhindert, dass in zu schneller Folge Daten an den Analytics Server übertragen werden.\"\n-\n name: _gid\n time: \"1 Tag\"\n desc: \"Speichert für jeden Besucher der Website eine anonyme ID. Anhand der ID können Seitenaufrufe einem Besucher zugeordnet werden.\"\n-\n name: _gac_<property-id>\n time: \"90 Tage\"\n desc: \"Wird verwendet um die Anforderungsrate einzuschränken.\"'),
(3,3,1,'matomo','Matomo','','','-\n name: _pk_id\n time: \"13 Monate\"\n desc: \"Speichert für jeden Besucher der Website eine anonyme ID. Anhand der ID können Seitenaufrufe einem Besucher zugeordnet werden.\"\n-\n name: _pk_ref\n time: \"6 Monate\"\n desc: \"Zur Speicherung des Verweises der ursprünglich zum Besuch der Website verwendet wurde.\"\n-\n name: _pk_ses\n time: \"30 Minuten\"\n desc: \"Hilfscookie für temporäre Daten\"\n-\n name: _pk_cvar\n time: \"30 Minuten\"\n desc: \"Hilfscookie für temporäre Daten\"\n-\n name: _pk_hsr\n time: \"30 Minuten\"\n desc: \"Hilfscookie für temporäre Daten\"'),
(4,4,1,'google-maps','Google Maps','Google LLC','','-\n name: /\n time: \"-\"\n desc: \"Google Maps ist ein Online-Kartendienst des US-amerikanischen Unternehmens Google LLC. Über die Google Maps API lassen sich Karten auf der Website einbetten.\"'),
(5,5,1,'facebook','Facebook Pixel','Facebook','','-\n name: _fbp\n time: \"3 Monate\"\n desc: \"Wird genutzt, um eine Reihe von Werbeprodukten anzuzeigen, zum Beispiel Echtzeitgebote dritter Werbetreibender.\"'),
(6,6,1,'phpsessid','Session','Diese Website','','-\n name: PHPSESSID\n time: \"Session\"\n desc: \"Die sog. Session-ID ist ein zufällig ausgewählter Schlüssel, der die Sessiondaten auf dem Server eindeutig identifiziert. Dieser Schlüssel kann z.B. über Cookies oder als Bestandteil der URL an ein Folgescript übergeben werden, damit dieses die Sessiondaten auf dem Server wiederfinden kann.\"'),
(7,7,1,'youtube','YouTube','Google','','-\n name: YSC\n time: \"Session\"\n desc: \"Registriert eine eindeutige ID, um Statistiken der Videos von YouTube, die der Benutzer gesehen hat, zu behalten.\"\n-\n name: VISITOR_INFO1_LIVE\n time: \"179 Tage\"\n desc: \"Versucht, die Benutzerbandbreite auf Seiten mit integrierten YouTube-Videos zu schätzen.\"\n-\n name: GPS\n time: \"Session\"\n desc: \"Registriert eine eindeutige ID auf mobilen Geräten, um Tracking basierend auf dem geografischen GPS-Standort zu ermöglichen.\"');
INSERT IGNORE INTO `%TABLE_PREFIX%iwcc_cookiegroup` (`pid`, `id`, `clang_id`, `domain`, `uid`, `prio`, `required`, `name`, `description`, `cookie`, `script`) VALUES
(1,1,1,'','required',1,'|1|','Notwendig','Notwendige Cookies ermöglichen grundlegende Funktionen und sind für die einwandfreie Funktion der Website erforderlich.', NULL, ''),
(2,2,1,'','statistics',2,NULL,'Statistik','Statistik Cookies erfassen Informationen anonym. Diese Informationen helfen uns zu verstehen, wie unsere Besucher unsere Website nutzen.','|google-analytics|','<script>\n	// Beispiel für das Nachladen von Google Analytics\n	// \'UA-12345678-9\' durch eigenes Property ersetzen\n	var gaProperty = \'UA-12345678-9\';\n\n	window.dataLayer = window.dataLayer || [];\n	function gtag(){dataLayer.push(arguments);}\n	gtag(\'js\', new Date());\n	gtag(\'config\', gaProperty , { \'anonymize_ip\': true });\n\n	if (gaProperty !== \'\' && gaProperty !== \'UA-XXXXXXXXX-X\') {\n		var gascript = document.createElement(\'script\');\n		gascript.async = true;\n		gascript.src = \'https://www.googletagmanager.com/gtag/js?id=\' + gaProperty;\n		document.head.appendChild(gascript);\n	}\n\n	/* Google Analytics Opt-Out */\n	var disableStr = \'ga-disable-\' + gaProperty;\n\n	if (document.cookie.indexOf(disableStr + \'=true\') > -1) {\n		window[disableStr] = true;\n	}\n	function gaOptout() {\n		document.cookie = disableStr + \'=true; expires=Thu, 31 Dec 2099 23:59:59 UTC; path=/\';\n		window[disableStr] = true;\n	}\n</script>'),
(3,3,1,'','external',4,NULL,'Externe Medien','Inhalte von Videoplattformen oder Kartendiensten sind standardmäßig deaktiviert. Wenn Cookies von externen Medien akzeptiert werden, bedarf der Zugriff auf diese Inhalte keiner manuellen Zustimmung mehr.','|google-maps|youtube|','<script type=\"text/javascript\">\n	// Beispiel für Nachladen von Google Maps via callback-Aufruf:\n	// initGmaps() muss definiert sein und den API-Aufruf starten\n	// key=XXX durch eigenen API-Key ersetzen\n	if ($(\'#deinGoogleMapsDivContainer\').length > 0) {\n			var script = document.createElement(\'script\');\n			script.type = \'text/javascript\';\n			script.src = \'https://maps.google.com/maps/api/js?key=XXX&callback=initGmaps\';\n			document.head.appendChild(script);														\n		}\n</script>'),
(4,4,1,'','marketing',3,NULL,'Marketing','Diese Cookies helfen uns bei der Analyse von Verbindungen zu und von Partnern sowie Kampagnen auf unserer Webseite. Durch diese Technologie werden Nutzer, die unsere Webseite bereits besucht und sich für unsere Webseite interessiert haben, durch zielgerichtete Werbung auf den Seiten der Partner erneut angesprochen.','|facebook|', '');
INSERT IGNORE INTO `%TABLE_PREFIX%iwcc_text` (`pid`, `id`, `clang_id`, `uid`, `text`) VALUES
(1, 1, 1, 'headline', 'Cookie-Verwaltung'),
(2, 2, 1, 'description', 'Wir verwenden Cookies, um Ihnen ein optimales Webseiten-Erlebnis zu bieten. Dazu zählen Cookies, die für den Betrieb der Seite und für die Steuerung unserer kommerziellen Unternehmensziele notwendig sind, sowie solche, die lediglich zu anonymen Statistikzwecken, für Komforteinstellungen oder zur Anzeige personalisierter Inhalte genutzt werden. Sie können selbst entscheiden, welche Kategorien Sie zulassen möchten. Bitte beachten Sie, dass auf Basis Ihrer Einstellungen womöglich nicht mehr alle Funktionalitäten der Seite zur Verfügung stehen.'),
(3, 3, 1, 'toggle_details', 'Details anzeigen/ausblenden'),
(4, 4, 1, 'provider', 'Anbieter:'),
(5, 5, 1, 'link_privacy', 'Datenschutzerklärung'),
(6, 6, 1, 'lifetime', 'Laufzeit:'),
(7, 7, 1, 'button_accept', 'Auswahl bestätigen'),
(8, 8, 1, 'button_select_all', 'Alle auswählen');
